export const convertXml = (dataFromForm: string) => {
	console.log(getHeaders(formToArray(dataFromForm) || []));
};

export const formToArray = (dataFromForm: string) => {
	var xmlParser = new DOMParser();
	var xmlDoc = xmlParser.parseFromString(dataFromForm, "text/xml");
	return xmlDoc
		.getElementsByTagName("CSVIntervalData")[0]
		.textContent?.split("\n");
};

export const getHeaders = (xmlDocArr: string[]) => {
	let header;
	xmlDocArr.forEach((row) => {
		if (row.includes("100")) header = row.split(","); //get the header row which contains 100
	});
	return header;
};

var A = [["n", "sqrt(n)"]];
for (var j: number = 1; j < 10; ++j) {
	A.push([j.toString(), Math.sqrt(j).toString()]);
}
var csvRows = [];

var csvString = csvRows.join("%0A");
var a = document.createElement("a");
a.href = "data:attachment/csv," + encodeURIComponent(csvString);
a.target = "_blank";
a.download = "myFile.csv";

document.body.appendChild(a);
a.click();
