import { SubmitHandler, useForm } from "react-hook-form";
import ReactDOM from "react-dom";
import { Labels } from "../utils/helper";

interface IFormInput {
	xmlInput: string;
}

const XMLInputForm = () => {
	const {
		register,
		handleSubmit,
		formState: { errors }
	} = useForm<IFormInput>();
	const onSubmit: SubmitHandler<IFormInput> = (data: IFormInput) =>
		alert(JSON.stringify(data.xmlInput));
	return (
		<div className="m-2 p-2">
			<form onSubmit={handleSubmit(onSubmit)}>
				<label>{Labels.xmlLabel}</label>
				<div className="mt-3">
					<textarea
						{...(register("xmlInput"), { required: true })}
						className="outline-black h-28 w-9/12"
					/>
					{errors.xmlInput && <span>This field is required</span>}
				</div>

				<div>
					<input type="submit" />
				</div>
			</form>
		</div>
	);
};

export default XMLInputForm;
