export const convertXml = (dataFromForm: string) => {
	console.log(formToArray(dataFromForm) || []);
};

const formToArray = (dataFromForm: string) => {
	var xmlParser = new DOMParser();
	var xmlDoc = xmlParser.parseFromString(dataFromForm, "text/xml");
	return xmlDoc
		.getElementsByTagName("CSVIntervalData")[0]
		.textContent?.split("\n");
};

//get the header row which contains 100
const getHeaders = (xmlDocArr: string[]) => {
	let header;
	xmlDocArr.forEach((row) => {
		if (row.includes("100")) header = row.split(",");
	});
	return header;
};

const toCSVarray = (unformattedRows: string[]) => {
	let CSVArray = [];
	let seperatedArr = seperateRowsToChunks(unformattedRows);
};

//seperate the rows by the line which contains 200
const seperateRowsToChunks = (unformattedRows: string[]) => {
	let chunks;
	let quickPointer = 0;
	let slowPointer = 0;
	unformattedRows.forEach((row, i) => {
		if (row.includes("200") || row.includes("900")) {
			if (quickPointer === 0) {
				quickPointer = i;
				slowPointer = i;
			} else {
				quickPointer = i;
				chunks.push(unformattedRows.slice(slowPointer, quickPointer));
				slowPointer = i;
			}
		}
	});
	return chunks;
};

